import React, { useState } from "react";
import { Line } from "react-chartjs-2";
import "chart.js/auto";
import "./App.css";

function App() {
  const [ip, setIp] = useState("");
  const [labels, setLabels] = useState([]);
  const [values, setValues] = useState([]);

  const fetchData = async () => {
    const res = await fetch(
      `http://localhost:8000/metrics?ip=${ip}&period_minutes=60&interval_seconds=60`
    );
    const data = await res.json();

    setLabels(data.map((d) => new Date(d.timestamp).toLocaleTimeString()));
    setValues(data.map((d) => d.value));
  };

  return (
    <div className="App">
      <h1>CPU Usage Viewer</h1>
      <input
        placeholder="Enter AWS internal IP (e.g. 172.31.88.161)"
        value={ip}
        onChange={(e) => setIp(e.target.value)}
      />
      <button onClick={fetchData}>Fetch Data</button>
      <Line
        data={{
          labels,
          datasets: [
            {
              label: "CPU Usage (%)",
              data: values,
              borderColor: "blue",
              tension: 0.3,
            },
          ],
        }}
      />
    </div>
  );
}

export default App;
